#include "leetcode.h"
vector<int> dp;
int dfs(string &s, int l) {
    if (l == s.size()) return 0;
    if (dp[l] != 0)
        return dp[l];
    int ll = l;
    while (ll < s.size() && s[ll] == s[l]) ll++;
    if (ll-l > 10) {
        dfs(s, ll-1);
        for (int i = ll-2; i >= l; i--)
            dp[i] = dp[i+1]+1;
        return dp[l];
    }
    dp[l] = 1;
    for (int i = l + 1; 2*i-l <= dp.size(); i++) {
        if (s.substr(l, i-l) == s.substr(i, i-l)) {
            dp[l] = max(dp[l], 1+dfs(s, i));
        }
    }
    return dp[l];
}
int deleteString(string s) {
    dp.resize(s.size(), 0);
    return dfs(s, 0);
}

int main(void) {
    cout << deleteString("");
}